int ETF (int a) {
    int num=a;
    for(int i=2;i*i<=a;i++) {
        if(a%i==0) {
            while(a%i==0) {
                a/=i;
            }
            num-=num/i;
        }
    }
    if(a>1){
        num-=num/a;
    }
    return num;
}

int gcd(int a, int b, int& x, int& y) {
    if (b == 0) {
        x = 1;
        y = 0;
        return a;
    }
    int x1, y1;
    int d = gcd(b, a % b, x1, y1);
    x = y1;
    y = x1 - y1 * (a / b);
    return d;
}

